guid.comment:
This is your Magento store's unique identifier.<script>
								function randomGUID(length, chars) {
								    var mask = '';
								    if (chars.indexOf('a') > -1) mask += 'abcdefghijklmnopqrstuvwxyz';
								    if (chars.indexOf('A') > -1) mask += 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
								    if (chars.indexOf('#') > -1) mask += '0123456789';
								    if (chars.indexOf('!') > -1) mask += '!&_';
								    var result = '';
								    for (var i = length; i > 0; --i) result += mask[Math.round(Math.random() * (mask.length - 1))];
								    return result;
								}
	                            var alert_guid = document.getElementById("insightdesigns_magentoalerts_settings_guid").value;
	                            if (typeof alert_guid == "undefined" || alert_guid == "") {
		                            // Generate a secure 1024 character unique key
		                            document.getElementById("insightdesigns_magentoalerts_settings_guid").value = randomGUID(1024,'#aA');
	                            }
	                            document.getElementById("insightdesigns_magentoalerts_settings_guid").setAttribute("readonly","true");
	                            document.getElementById("insightdesigns_magentoalerts_settings_guid").style.backgroundColor = "#DEDEDE";
                            </script>
